import {Component, Output, EventEmitter} from '@angular/core';

@Component({
  selector: 'app-keypad',
  templateUrl: './keypad.component.html',
  styleUrls: ['./keypad.component.css']
})
export class KeypadComponent {
  @Output() selection: any = new EventEmitter<string>();
  double: boolean = false;
  treble: boolean = false;
  dart: number = 0;
  // throwResult: string;
  btnColour= 'red';

  sendSelection(throwResult: any): void{
      if(throwResult === "BACK"){
        this.restorePrevious();
      }

      if(this.double) this.selection.emit("D" + throwResult);
      if(this.treble) this.selection.emit("T" + throwResult);

      this.double = false;
      this.treble = false;
      this.dart++;
      this.selection.emit("S" + throwResult);
  }

  restorePrevious(): void{
    // TODO
  }

  turnManager(throwResult: string): void{
    if(this.dart == 0 && throwResult === "BACK"){
      // TODO send previous player selection
    }

    this.sendSelection(throwResult);

    // after the selection it check and advances automatically
    if(this.dart == 3) {
      // TODO send next player input
    }
  }

  changeDouble() {
    this.double = !this.double;
    if(this.dBtnColour === 'green') this.btnColour = 'red';
    if(this.dBtnColour === 'red') this.btnColour = 'green';
    console.log(this.double);
  }
  changeTriple() {
    this.treble = !this.treble;
    if(this.tBtnColour === 'green') this.btnColour = 'red';
    if(this.tBtnColour === 'red') this.btnColour = 'green';
    console.log(this.treble);
  }
}
<div class="keypad-main">
  <div class="side-buttons">
    <button mat-flat-button color="accent" class="side-button" value="delete" (click)="turnManager('BACK')">Back</button>
    <button mat-flat-button color="warn" class="side-button" value="accept">Submit</button>
  </div>
  <div class="keypad-common">
    <button mat-flat-button color="primary" value="1" (click)="turnManager('1')">1</button>
    <button mat-flat-button color="primary" value="2" (click)="turnManager('2')">2</button>
    <button mat-flat-button color="primary" value="3" (click)="turnManager('3')">3</button>
    <button mat-flat-button color="primary" value="4" (click)="turnManager('4')">4</button>
    <button mat-flat-button color="primary" value="5" (click)="turnManager('5')">5</button>
    <button mat-flat-button color="primary" value="6" (click)="turnManager('6')">6</button>
    <button mat-flat-button color="primary" value="7" (click)="turnManager('7')">17</button>
    <button mat-flat-button color="primary" value="8" (click)="turnManager('8')">8</button>
    <button mat-flat-button color="primary" value="9" (click)="turnManager('9')">9</button>
    <button mat-flat-button color="primary" value="10" (click)="turnManager('10')">10</button>
    <button mat-flat-button color="primary" value="11" (click)="turnManager('11')">11</button>
    <button mat-flat-button color="primary" value="12" (click)="turnManager('12')">12</button>
    <button mat-flat-button color="primary" value="13" (click)="turnManager('13')">13</button>
    <button mat-flat-button color="primary" value="14" (click)="turnManager('14')">14</button>
    <button mat-flat-button color="primary" value="15" (click)="turnManager('15')">15</button>
    <button mat-flat-button color="primary" value="16" (click)="turnManager('16')">16</button>
    <button mat-flat-button color="primary" value="17" (click)="turnManager('17')">17</button>
    <button mat-flat-button color="primary" value="18" (click)="turnManager('18')">18</button>
    <button mat-flat-button color="primary" value="19" (click)="turnManager('19')">19</button>
    <button mat-flat-button color="primary" value="20" (click)="turnManager('20')">20</button>
  </div>
  <div class="bottom-buttons">
    <button mat-flat-button color="primary" value="25">25</button>
    <button mat-flat-button color="warn" [style.background-color]="btnColour" class="special-button" value="double" (click)="change(double)">Double</button>
    <button mat-flat-button color="warn" [style.background-color]="btnColour" class="special-button" value="treble" (click)="change(treble)">Treble</button>
    <button mat-flat-button color="accent" value="25" (click)="turnManager('MISS')">Miss</button>
  </div>
</div>
