import { Component } from '@angular/core';
import {Player} from "../interfaces/Player";
import {BehaviorSubject, Observable, tap} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  id: number = 0;
  private playerSubject = new BehaviorSubject<Player[]>([{
    id: this.id,
    name: "",
    score: 501,
    legResult: [],
    isCurrent: true
  }])
  players$: Observable<Player[]>;
  playerNum: number = 1;

  gameHasStarted: boolean = false;

  increment(): void{
    this.id++;
    if(this.playerNum < 4){
      this.playerNum += 1;
      this.players$.pipe(tap(players => {
        players.push({
          id: this.id,
          name: "",
          score: 501,
          legResult: [],
          isCurrent: false
        })
      })).subscribe();
      console.log(this.playerNum)
    }
  }
  decrement(): void{
    this.id--;
    if(this.playerNum > 1){
      this.playerNum -= 1;
      this.players$.pipe(tap(players => {
        players.pop()
      })).subscribe()
    }
    console.log(this.players$)
  }

  ngOnInit() {
    this.players$ = this.playerSubject
  }

  startGame(){
    this.gameHasStarted = true;
  }

  stopGame(){
    this.gameHasStarted = false;
  }

  addThrow(newThrow: string): void{
    console.log(newThrow);
    this.players$.subscribe(player => {
      console.log(player);
    })
  }
}
<div class="home-main">
  <div class="players-selection" *ngIf="!gameHasStarted">
    <h2>
      Select The Players Here
    </h2>
    <div class="counter">
      <button type="text" class="btn btn-primary" (click)="decrement()">-</button>
      <h1>{{playerNum}}</h1>
      <button type="text" class="btn btn-primary" (click)="increment()">+</button>
    </div>
    <button class="ready-button" mat-flat-button color="warn" value="1" (click)="startGame()">Ready</button>
  </div>
  <div class="game-header" *ngIf="gameHasStarted">
    <h2>
      Game In Progress...
    </h2>
<!--    <div class="counter">-->
<!--      <button type="text" class="btn btn-primary" (click)="decrement()">-</button>-->
<!--      <h1>{{playerNum}}</h1>-->
<!--      <button type="text" class="btn btn-primary" (click)="increment()">+</button>-->
<!--    </div>-->
    <button class="stop-button" mat-flat-button color="warn" value="1" (click)="stopGame()">Abort Game</button>
  </div>
  <div class="players-boxes" >
    <app-player-box *ngFor="let player of players$ | async" [player]="player" [gameHasStarted]="gameHasStarted" />
  </div>
  <div class="keypad">
    <app-keypad (selection)="addThrow($event)"/>
  </div>
</div>
