import { Component } from '@angular/core';
import {Player} from "../interfaces/Player";
import {Observable} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  players$!: Observable<Player>;
  playerNum: number = 1;

  increment(){
    this.playerNum += 1;
  }
  decrement(){
    if(this.playerNum >1){
      this.playerNum -= 1;
    }
  }
}
