import {Component, Input} from '@angular/core';
import {Player} from "../../interfaces/Player";

@Component({
  selector: 'app-player-box',
  templateUrl: './player-box.component.html',
  styleUrls: ['./player-box.component.css'],
})
export class PlayerBoxComponent {
  @Input() player: Player;
  progress: number = 50;
  @Input()
  gameHasStarted: boolean;
}
<div class="main-box">
  <td><mat-form-field class="example-full-width">
    <mat-label>Player</mat-label>
    <input [readonly]="gameHasStarted"
           matInput [value]="player.name"
           style="font-size: 36px; text-transform: uppercase;">
  </mat-form-field></td>
  <h1 style="font-size: 180px">
    {{player.score}}
  </h1>
  <mat-progress-bar mode="determinate"
                    [value]="(501 - player.score) / 501 * 100" />
  <table mat-table [dataSource]="dataSource" class="mat-elevation-z8 demo-table"></table>
  <title>Stats</title>
  <title>Checkouts</title>
</div>
