import { Component } from '@angular/core';
import {Player} from "../interfaces/Player";
import {BehaviorSubject, Observable, tap} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  private playerSubject = new BehaviorSubject<Player>({
    id: 0,
    name: "Insert Name",
    score: 501,
    legResult: []
  })
  players$: Observable<Player[]>;
  playerNum: number = 1;

  // dummyPlayer: Player = {
  //   id: 0,
  //   name: "Insert Name",
  //   score: 501,
  //   legResult: []
  // }

  increment(): void{
    this.playerNum += 1;
    console.log(this.players$);
  }
  decrement(): void{
    if(this.playerNum >1){
      this.playerNum -= 1;
    }
  }

  ngOnInit() {
    this.count$ = this.counterSubject
  }

  ngOnInit(){
    this.players$.pipe(tap(players => {
      players.push(this.dummyPlayer)
    }))
  }
}
<div>
  <div class="players-selection">
    <h2>
      Select Players Here
    </h2>
    <div class="counter">
      <button type="text" class="btn btn-primary" (click)="decrement()">-</button>
      <h1>{{playerNum}}</h1>
      <button type="text" class="btn btn-primary" (click)="increment()">+</button>
    </div>
  </div>
  <div class="players-boxes" *ngFor="let player of players$ | async">
    <app-player-box [player]="player" />
  </div>
  <div>
    <div *ngIf="(players$ | async)?.length==0">No records found.</div>
    <h2>{{(players$ | async)?.length}}</h2>
  </div>
</div>
