import { Component } from '@angular/core';
import {Player} from "../interfaces/Player";
import {Observable, tap} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  players$!: Observable<Player[]>;
  playerNum: number = 1;

  dummyPlayer: Player = {
    id: 0,
    name: "Insert Name",
    score: 501,
    legResult: []
  }

  increment(): void{
    this.playerNum += 1;
  }
  decrement(): void{
    if(this.playerNum >1){
      this.playerNum -= 1;
    }
  }

  ngOnInit(){
    this.players$.pipe(tap(players => {
      players.push(this.dummyPlayer)
    }))
  }
}
<div class="players-selection">
  <h2>
    Select Players Here
  </h2>
  <div class="counter">
    <button type="text" class="btn btn-primary" (click)="decrement()">-</button>
    <h1>{{playerNum}}</h1>
    <button type="text" class="btn btn-primary" (click)="increment()">+</button>
  </div>
</div>
<!--<ng-container *ngForOf="(players$ | async) as player">-->
<div *ngForOf="let player of players$ | async">
 <app-player-box [player]="player">

 </app-player-box>
</div>
